%count TTL pulsesclear all;display=getScannerDisplay(5)trigger = 34;ticwhile 1    WaitSecs(0.001);    [keyIsDown, secs, keycode] = PsychHID('KbCheck',display.forpnum);    if keycode(trigger)        firstpulse = secs;        pulsetimes = [0];        break    endendfor counter = 1:50    WaitSecs(0.1);    flag = 0;    clear keycode    while flag == 0        WaitSecs(0.001);        [keyIsDown, secs, keycode] = PsychHID('KbCheck',display.forpnum);        if keycode(trigger)            pulsetimes = [pulsetimes (secs-firstpulse)];            flag = 1;            toc        end    endendmean(diff(pulsetimes))max(diff(pulsetimes))min(diff(pulsetimes))figurehist(diff(pulsetimes))