function image_Spokes = makeSpokes_OSX(display,params);outer = angle2pix(display, params.size(2));spokewidth = angle2pix(display, params.SpokeWidth);xx = linspace(-params.size(2), params.size(2), (outer * 2));[x, y] = meshgrid(xx);r = sqrt(x.^2 + y.^2);%tmp = atan2(y,x)/pi*4+0.5;%tmp = abs(tmp - round(tmp));%tmp = abs(tmp - 0.5);%spokes = (r > params.size(1) & r < params.size(2) & tmp < 0.01);mask = r > params.size(1) & r < params.size(2);spokes = zeros(length(xx), length(xx));spokes(:,((round(length(xx)/2)))) = 1;spokes(((round(length(xx)/2))),:) = 1;for counter = 1:round(spokewidth/2)	spokes(:,((round(length(xx)/2)) - counter)) = 1;	spokes(:,((round(length(xx)/2)) + counter)) = 1;	spokes(((round(length(xx)/2)) - counter),:) = 1;	spokes(((round(length(xx)/2)) + counter),:) = 1;end% So far, we have made spokes along the cardinal axes%spokes = ceil(((spokes + imrotate(spokes, (360 / params.regions), 'crop') + ...	%imrotate(spokes, (720 / params.regions), 'crop')) .* mask) / 2);spokes = ceil(((spokes + imrotate(spokes, (360 / params.regions), 'crop') + ...	imrotate(spokes, (720 / params.regions), 'crop') + imrotate(spokes, (1080 / params.regions),'crop')) .* mask) / 2);	image_Spokes = spokes * params.StaRange(1);%need to do antialiasing here