function gammaValues = cmeterNECgamma(tsize, gunColors, fbValues, monitor, ...									  synchFreq, hitKey);%% gammaValues = cmeterNECgamma(tsize, gunColors, fbValues, monitor, synchFreq, hitKey);%% AUTHOR:  		Heidi Baseler% DATE:			02.12.98% HISTORY:		Adapted for NEC LCD2000 from "cmeterGammaCalib.m"%				written by Xuemei Zhang on Turquoise, 08.98% 				08/05/99 RFD added code to deal with a photometer reset problem.%				9/13/00 Ress Fixed compatibility problem with G4 hardware, and%				cleaned up text prompts on screen.%% PURPOSE: 		Measure gamma curve using cmeter%% INPUTS:% tsize:		Size of target square, as [ysize xsize].%          		If tsize has 4 elements, treat as rectangle:%          		[ysize xsize ycenter xcenter].%         	 	If tsize has 1 element, target is a square.% gunColors:	Which guns to measure. Should be in the range [0 1].%              	For example, to measure red gun use [1 0 0].% fbValues:		Levels of framebuffer values to measure.%             	(between 0 and 255 for 8-bit card, 0 and 1023 for 10 bit card)% monitor:		Use 0 for console, 1 for second monitor (check monitor numbers)% synchFreq:	Refresh rate of monitor (can be floating point number)% hitKey:		optional.  A value of 1 means that the user will have%				to hit a key before calibration begins.  A value of 0 means that%				the calibration will begin directly.  Default=1.if (length(tsize)==1)  tsize = [tsize tsize];endif (nargin<6)	hitKey = 1;end% Open screen and find center[screenPtr, screenRect] = screen(monitor, 'OpenWindow', 0, [], 8);HideCursor;if (length(tsize)==4)  xc = tsize(4);  yc = tsize(3);else  xc = (screenRect(RectRight) - screenRect(RectLeft))/2;  yc = (screenRect(RectBottom) - screenRect(RectTop))/2;endxc0 = xc - 150;yc0 = yc + 150;targetClut = 1;bgClut = 0;textClut = 255;targetMap = gray(256) * max(fbValues);targetMap(bgClut+1,:) = [0 0 0];		   targetMap(targetClut+1,:) = [1 1 1] * max(fbValues);		   targetMap(textClut+1,:) = [1 1 1] * max(fbValues);	screen(screenPtr, 'SetClut', targetMap);		   % Display a target so that we can aim and focus camera:if (hitKey==1)	targetRect([RectTop RectLeft RectBottom RectRight])= ...           		[yc xc yc xc]+[-1 -1 1 1]*50;	screen(screenPtr, 'FillRect', targetClut, targetRect);	screen(screenPtr, 'TextFont', 20);	screen(screenPtr, 'TextSize', 16);	screen(screenPtr, 'TextStyle', 1);	[xc1, yc1] = screen(screenPtr, 'DrawText', 'Aim and focus camera, then press any key', ...                        xc0, yc0, textClut);	textRect = [xc0 yc0-20 xc1 yc1+10];	GetChar;	screen(screenPtr, 'FillRect', bgClut, textRect);	[xc1, yc1] = screen(screenPtr, 'DrawText', 'OK.  You have 10 seconds to leave the room', ...    	                           xc0, yc0, textClut);	textRect = [xc0 yc0-20 xc1 yc1+10];	%pause(10); % Give you enough time to leave the room and shut the doorend% Erase text, draw calibration targetscreen(screenPtr, 'FillRect', bgClut, textRect);screen(screenPtr, 'FillRect', bgClut, targetRect);targetRect([RectTop RectLeft RectBottom RectRight])= ...           [yc xc yc xc]+[-tsize(1) -tsize(2) tsize(1) tsize(2)]/2;screen(screenPtr, 'FillRect', targetClut, targetRect);% prepare camera%%s = cmeter('Init');                 %% initialize camera%%cmeter('SetParams', 0, 0, 1);       %% set synch,integration time,avgcnt%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%% Gamma measurementavgcnt = 1;gammaValues = zeros(length(fbValues), size(gunColors,1));for gun = 1:size(gunColors,1)  for fb = 1:length(fbValues)    targetMap(targetClut+1,:) = gunColors(gun,:) * fbValues(fb);% Must explicitly set Gamma to 10 bits!     screen(screenPtr, 'Gamma', targetMap, 10);		       screen(screenPtr, 'SetClut', gray(256)*255);		%%while cmeter('Init') == -1	%%	disp('Reset power to spectrophotometer')	%%	pause(5);	%%end;    %%integrationTime = cmeterGetTime(synchFreq);    %%cmeter('SetParams', 0, integrationTime, avgcnt);    %%s = cmeter('Measure', integrationTime*(avgcnt+2));      %%xyz = cmeter('XYZ');         %% get spectrum    %%gammaValues(fb, gun) = xyz(2);  endendShowCursor;screen(screenPtr, 'Close');