function testGamma(gammaFile)% Script to test that the gamma correction is successful% HAB, 02.16.98.  Modified by WAP, 7.10.98.load(gammaFile);inds = fbValues+1;linGammaValues = zeros(length(inds),3);linFBvalues = 1023 * invGamma10(inds,:);for k=1:size(gunColors,1)	tgunColors = gunColors(k,:);	tFBvalues = linFBvalues(:,k);	linGammaValues(:,k) = cmeterNECgamma(targetSize, tgunColors, tFBvalues, monitor, synchFreq, 0);end% Plot outputplot(1:length(inds),linFBvalues(:,1),'r',1:length(inds),linFBvalues(:,2),'g',1:length(inds),linFBvalues(:,3),'b')figure;plot(1:length(inds),linGammaValues(:,1),'r',1:length(inds),linGammaValues(:,2),'g',1:length(inds),linGammaValues(:,3),'b')