function checkMonitorValues(gammaFile,origFreq,origSize,origMonitor)% Makes sure that the monitor parameters are the same for doing the% spectral analysis as were used for the gamma correction.  WAP 7.10.98.load(gammaFile);if (synchFreq~=origFreq | targetSize~=origSize | monitor~=origMonitor)	origFreq	synchFreq	origSize	targetSize	origMonitor	monitor	error('Parameters do not match between spectrum and gamma calibrations.');end