function v=fitResultsPsycho(staircase,results,fitFuncName)if ~exist('fitFuncName')	fitFuncName = 'fitWeibull';endif staircase.logFlag	results.intensity = log(results.intensity);endgoodVals = finite(results.response);p=polyfit(results.intensity(goodVals),results.response(goodVals),1);%starting values for the fit%mean is the mean of all stimulus presentationsu = mean(mean(results.intensity));%slope is proportional to the mean intensity for correct responses -%mean intensity for incorrect responses.s= (mean(results.intensity(find(results.response)))- ...    mean(results.intensity(find(1-results.response))))*40;v = fmins(fitFuncName,[u,s],0,[],results);