function plotFitPsycho(staircase,results,fitFuncName)%plotFitPsycho(staircase)if ~exist('fitFuncName')	fitFuncName = 'weibull';endintensity = linspace(min(min(results.intensity)),max(max(results.intensity)),100);%Come up with some initial guesses for u and sgoodVals = finite(results.response);if staircase.logFlag	p=polyfit(log(results.intensity(goodVals)),results.response(goodVals),1);else	p=polyfit(results.intensity(goodVals),results.response(goodVals),1);endu = (0.7939-p(2))/p(1);s = p(2);[u,v] = fmins(fitFuncName,[u,s],0,[],results)