function [intensity,n,pc]=plotResultsPsycho(staircase,results,v,psychoFuncName)[n,pc,intensityList] = compileIntensity(results);if staircase.logFlag	intensityList=log(intensityList);endfor i=1:length(intensityList)	plot(intensityList(i),pc(i),'o','MarkerSize',(n(i)));	hold on		endxlabel(staircase.intensityParamName);ylabel('Percent correct');set(gca,'XLim',[min(intensityList),max(intensityList)]);set(gca,'YLim',[min(min(pc),0.45),1.05]);set(gca,'XTick',intensityList);gridwiden%Plot fitted psychometric function, if the parameters (v) are given.if ~exist('v')	hold off	if staircase.logFlag		logx2raw	end		returnendif ~exist('psychoFuncName')	psychoFuncName = 'weibull';endfitX = linspace(min(intensityList),max(intensityList),100);evalstr = ['fitY = ',psychoFuncName,'(v,fitX);'];eval(evalstr)plot(fitX,fitY,'g','LineWidth',2);%draw lines to mark thresholdyLim = get(gca,'YLim');plot([min(intensityList),v(1),v(1)],[.7939,.7939,yLim(1)],'r');if staircase.logFlag	logx2rawendhold offif staircase.logFlag	title(['Threshold: ',num2str(exp(v(1)))])else	title(['Threshold: ',num2str(v(1))])end