function plotResultsStaircase(staircase,results,v)%plotResultsStaircase(staircase,results,v)x = [1:size(results.intensity,1)];if staircase.logFlag	stairs(log(results.intensity))else	stairs(results.intensity)end[n,pc,intensityList] = compileIntensity(results);hold onxLim = get(gca,'XLim');if staircase.logFlag	plot(xLim,log([results.testIntensity,results.testIntensity]),'k');	intensityList = log(intensityList);else	plot(xLim,[results.testIntensity,results.testIntensity],'k');endset(gca,'YTick',intensityList);set(gca,'XLim',[1,size(results.intensity,1)+1]);gridxlabel('trial');ylabel(staircase.intensityParamName);if staircase.logFlag	logy2raw;endif exist('v') %draw horizontal line at threshold intenisty	line(xLim,[v(1),v(1)],'Color','r');end		hold off