function clickMouse2Begin(display,dispString)% clickMouse2Begin(display,[dispString])%%   Displays 'please click the mouse to begin' on the SCREEN%   just below the center of the screen and waits for the %   user to click the mouse.%%   Optional string 'dispString' is displayed just above the%   center of the screen.%% SEE ALSO pressKey2Begin dispStringInCenter pressKey2Begin% 1/20/97 gmb wrote it.pressString = 'please click the mouse to begin.';dispStringInCenter(display,pressString,0.55,'white');if exist('dispString')	dispStringInCenter(display,dispString,0.45,'white');endmouseClick;dispStringInCenter(display,pressString,0.55,'background');if exist('dispString')	dispStringInCenter(display,dispString,0.45,'background');end