function dispStringInCenter(display,dispString,vLoc,colorName,textSize)%dispStringInCenter(display,dispString,[vLoc],[colorName],[textSize]);if ~exist('vLoc')	vLoc = 0.5;endif ~exist('colorName')	colorName = 'white';endif ~exist('textSize')	textSize = 20;endSCREEN(display.windowPtr,'TextSize',textSize);charWidth = textSize/4.5;col = display.reservedColor(findName(display.reservedColor,colorName)).fbVal;if (display.stereoFlag==0)	loc(1) = display.rect(3)/2-charWidth*length(dispString);	loc(2) = display.rect(4)*vLoc;	SCREEN(display.windowPtr,'DrawText',dispString,loc(1),loc(2),col);else	loc(1) = display.rect(3)/4-charWidth*length(dispString);	loc(2) = display.rect(4)*vLoc;	loc(3) = 3*display.rect(3)/4-charWidth*length(dispString);	SCREEN(display.windowPtr,'DrawText',dispString,loc(1),loc(2),col);	SCREEN(display.windowPtr,'DrawText',dispString,loc(3),loc(2),col);end