function dispStringInCenterFast(display,dispString,vLoc,colorNum,textSize)%dispStringInCenterFast(display,dispString,vLoc,colorNum,textSize);%%Nothing is optional for this sucker.  Bwahahahahahahaha!SCREEN(display.windowPtr,'TextSize',textSize);charWidth = textSize/4.5;if (display.stereoFlag==0)	loc(1) = display.rect(3)/2-charWidth*length(dispString);	SCREEN(display.windowPtr,'DrawText',dispString,loc(1),vLoc,colorNum);else	loc(1) = display.rect(3)/4-charWidth*length(dispString);	loc(2) = 3*display.rect(3)/4-charWidth*length(dispString);	SCREEN(display.windowPtr,'DrawText',dispString,loc(1),vLoc,colorNum);	SCREEN(display.windowPtr,'DrawText',dispString,loc(2),vLoc,colorNum);end