clear mex;display = usePredefinedDisplay('screenNumber', 1, 'resolution', '1024x768, 75Hz', ...			'cmapDepth', 10);display = openScreen(display);image = repmat([0:255], 64, 1);image2 = fix(image./4)*4;image3 = ones(64, 256)*127;%imagesc(image2);center = centerRect([0 0 512 128],display.rect);SCREEN(display.windowPtr, 'PutImage', image3, offsetRect(center,0,-192));SCREEN(display.windowPtr, 'PutImage', image, offsetRect(center,0,0));SCREEN(display.windowPtr, 'PutImage', image2, offsetRect(center,0,192));offset = 0;tenbitCmap = [offset:offset+255]'*[1 1 1];[cmap, gamma] = tenBit2eightBit(display, tenbitCmap);SCREEN(display.windowPtr, 'Gamma', gamma, 10);SCREEN(display.windowPtr, 'SetClut', cmap, 0);