function [stimParams, scanParams] = initParams;% AUTHOR:  Press and Backus% PURPOSE: Set up the parameters for runScan demo%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%                                               %% These parameters are specific to the stimulus %% generation code being used in the demo.  The  %% stimulus generation code is not generally the %% same for every experiment.                    %%                                               %% These are the paramaters for making a single  %% instance of the stimulus.                     %%                                               %%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%stimParams.stimulusType = 'radial'; 		%'linear,' 'radial,' or 'target'stimParams.waveType = 'sinusoid';			%'sinusoid' or 'square'stimParams.envelopeType = 'none';			%'none,' 'gaussian,' or 'raisedcos'stimParams.temporalType = 'drift'; 			%'flickerSinusoid,' 'flickerSquare,'											%'drift,' or 'static'% Spatial informationstimParams.size = 16; 					%deg: [x x] for rect dims, x for circle diameterstimParams.spatialFrequency = 0.5; 		%cpdstimParams.spatialPhase = 0;			%degreesstimParams.coldir = [1 1 1];stimParams.contrast = 1;% Temporal informationstimParams.duration = 1.02;				% Stimulus durationstimParams.temporalFrequency = 4; 		%cps (positive => outward/rightward)stimParams.temporalPhase = 0;			%seconds% Fixation parametersfix.colors = [1 2]; 		% Reserved colors in cmapfix.size = 0.5;				% Degreesfix.position = [0 0];stimParams.fix = fix;%%% Scanning parameters %%%% Parameters relevant to each trialscanParams.trialDuration = 4;scanParams.ISIDuration = 0.2; 			% The duration of the ISI between stimulusscanParams.responseDuration = 0.5;		%seconds% Parameters relevant to entire scanscanParams.ncycles = 1;scanParams.period = 36;scanParams.scanDuration = scanParams.period * scanParams.ncycles;