/* SerialLib.h*/#include <Devices.h>#include <Serial.h>#define		SERBUFSIZ	10000L#define		XONCHAR		0x11#define		XOFFCHAR	0x13#define		ESCAPECHAR	0x1B// Structure to hold serial driver information.// This structure is passed back and forth between routines.typedef struct {	Str255 inDriver;	Str255 outDriver;	short inRefNum;	char *inCharBuf;	char *inMgrBuf;	short outRefNum;	} SerialInfo;// Prototypes for SerialLib.cvoid 	SerialInit(SerialInfo *info, char *inDriver, char *outDriver);void	SerialSetDrivers(SerialInfo *info, char *inDriver, char *outDriver);void	SerialSetSpeed(SerialInfo *info, short speed);void 	SerialClose(SerialInfo *info);long	SerialCharsAvail(SerialInfo *info);long	GetSerialChars(SerialInfo *info, long count);long  	SerialWriteNL(SerialInfo *info, char *msg);long  	SerialWriteCR(SerialInfo *info, char *msg);long  	SerialWrite(SerialInfo *info, char *msg);long  	SerialWriteN(SerialInfo *info, char *msg, int count);// Storage space used to reference open drivers.//#ifdef MAIN//  char 	*inbuf = NULL, *sermgrbuf = NULL;//  short	aOutRefNum = -1, aInRefNum = -1;//#else//  extern char *inbuf, *sermgrbuf;//  extern short aOutRefNum, aInRefNum;//#endif