function [n,pc,intensityList] = compileIntensity(results)%[n,pc,intensityList] = compileIntensity(results)[sortedIntensities,id] = sort(results.intensity(:));intensityList = [sortedIntensities(diff(sortedIntensities)>0.0001);sortedIntensities(length(sortedIntensities))];n = ones(size(intensityList));pc = ones(size(intensityList));for i=1:length(intensityList)			ids = (abs(results.intensity-intensityList(i))<0.0001);	n(i) = sum(sum(ids));	pc(i) = sum(sum(results.response(ids)))/ n(i);endif sum(n) ~= prod(size(results.response))	disp('Error: compileIntensity miscounted');end