function expLetive(numCusses)%expLetive(numCusses)if ~exist('numCusses')	numCusses = ceil(randn(1)^2);endcussList = str2mat('shit','fuck','god dammit','suck','jeeezus','crap',...                   'motherfucker','piss','bite me','bastard','bitch',...				   'rat fart','fleet');				   				   cussNum = floor(rand(1,numCusses)*size(cussList,1))+1;for i=1:numCusses	speak(cussList(cussNum(i),:))end