function display = usePredefinedDisplay(varargin);% display = usePredefinedDisplay([property, value, ...]);%% Created by BTB and WAP on 9/4/98%% The following properties are allowed: 'monitor' 'card' 'resolution' 'position'%	'screenNumber' 'stereoFlag' and 'distance' (distance would override the settings%	determined by the 'position' parameter).	if mod(nargin,2)	error('Number of arguments must be even (propertyName, propertyValue, ...).');end%%%%% Specify default values for all parameters %%%%%%%% Specify values that don't tend to depend upon locationcard = 'Radius10-bit';stereoFlag = 0;					% Assume not in stereocmapDepth = 8;% Specify/change values that do depend on locationdisp(['Initializing ' machineName ' display.']);if strcmp(machineName, 'vermillion')	screenNumber = 1;   %should be 0 or 1	position = 'room 452';	monitor = 'NecLcd2000';	resolution = '640x480, 67Hz';elseif strcmp(machineName, 'Pink')	screenNumber = 1;   	position = 'room 454';	monitor = 'RadiusGrayscale';	resolution = '1152x870, 75Hz';elseif strcmp(machineName, 'Burgundy')	screenNumber = 1;   	position = 'room 485';	monitor = 'ViewSonic';	resolution = '640x480, 67Hz';elseif strcmp(machineName, 'Turquoise')	screenNumber = 1;   	position = 'room 485';	monitor = 'HP';	resolution = '1024x768, 75Hz';elseif strcmp(machineName, 'Black')    screenNumber = 1;    position = 'outside bore';    monitor = 'SanyoLCD';    resolution = '640x480, 67Hz';elseif strcmp(machineName, 'Ochre')	screenNumber = 0;   	position = 'shielded box';	monitor = 'NecLcd2000';	resolution = '640x480, 67Hz';elseif strcmp(machineName, 'Ochre3')	screenNumber = 1;   	position = 'shielded box';	monitor = 'NecLcd2000';	resolution = '640x480, 67Hz';else	disp('Using default display:')	screenNumber = 1   	position = 'room 452'	monitor = 'NecLcd2000'	resolution = '640x480, 67Hz'end%%%%% Adjust any parameters as per special requests %%%%%for argNum = 1:(nargin/2)	propertyName = varargin{2*argNum-1};	propertyVal = varargin{2*argNum};	switch propertyName		case 'monitor', 		monitor = propertyVal;		case 'card', 			card = propertyVal;		case 'resolution', 		resolution = propertyVal;		case 'position', 		position = propertyVal;		case 'screenNumber', 	screenNumber = propertyVal;		case 'stereoFlag', 		stereoFlag = propertyVal;		case 'distance', 		distance = propertyVal;		case 'cmapDepth',		cmapDepth = propertyVal;		otherwise,			error(['Unknown propertyName: ' propertyName]);	end % switch propertyName	end	% for argNum = ...if exist('distance')	display = initDisplayStruct(monitor,card,resolution,position,screenNumber, ...				stereoFlag,cmapDepth,distance);else	display = initDisplayStruct(monitor,card,resolution,position,screenNumber, ...				stereoFlag,cmapDepth);end