function cmap = insertSwappedReservedCols(display,cmap)%cmap = insertSwappedReservedCols(display,cmap)if isfield(display,'reservedColor')	for colNum = 1:size(display.reservedColor,2)		for cmapNum = 1:size(cmap,3)			cmap(display.reservedColor(colNum).fbVal+1,:,cmapNum)=...			display.reservedColor(colNum).gunVal;		end	end	if size(display.reservedColor,2) >= 3 ...			& ~strcmp(display.reservedColor(2).name,'background') ...			&  ~strcmp(display.reservedColor(3).name,'background')		for cmapNum = 1:size(cmap,3)			cmap(display.reservedColor(2).fbVal+1,:,cmapNum) = ...				display.reservedColor(3).gunVal;			cmap(display.reservedColor(3).fbVal+1,:,cmapNum) = ...				display.reservedColor(2).gunVal;		end	endend