function fixImage = fixLargeCross(display,images,fixParams)%% AUTHOR:  WANDELL% DATE:% PURPOSE:if ~isfield(fixParams,'lineWidth')	lineWidth = 0;else	lineWidth = fixParams.lineWidth;endxOffset = angle2pix (display, fixParams.position(2));yOffset = angle2pix (display, fixParams.position(1));[nRow nCol] = size(images(:,:,1));if nRow ~= nCol	error('fixLargeCross:  We only handle square images for now');end[X Y] = meshgrid(1:nCol, 1:nRow);X = X-nCol/2+xOffset;Y = Y-nRow/2+yOffset;fixImage = ((abs(X-Y)<=lineWidth/2) | ...			(abs(X+Y)<=lineWidth/2)) * -1; % The thickness is not perfectly											% determined by the lineWidthreturn;