function [left,right] = insertNoniusBox(left,right,radius,twoColors)%[left,right] = insertNoniusCross(left,right,radius,twoColors)% inserts nonius lines into disparity images along with a% binocular fixation cross% radius: half-size of image (true size is [2*radius+1,2*radius+1], default=9% twoColors gives color of background and lines (default [0 1])% DJF% BTB 9/16/98 modified from insertNoniusCross.mif ~exist('radius')  radius=7;endif radius < 3 error('radius argument must be at least 3.'); endwidth=2*radius+1;if ~exist('twoColors')  twoColors=[0 1];end%get image sizes, and center locationsm = size(left,1);n = size(left,2);mdiv2=round(m/2);ndiv2=round(n/2);%insert black box in center:%black box alonebox = twoColors(1)*ones(2*radius+1,2*radius+1);% % fixation cross% box(radius:radius+2,radius+1)=twoColors(2)*ones(3,1);% box(radius+1,radius:radius+2)=twoColors(2)*ones(1,3);leftBox=box;leftBox(radius-5:radius-2,radius+1)=twoColors(2)*ones(4,1);leftBox(radius+1,radius-5:radius-2)=twoColors(2)*ones(1,4);leftBox(1,:)=twoColors(2)*ones(1,width);leftBox(width,:)=twoColors(2)*ones(1,width);leftBox(:,1)=twoColors(2)*ones(width,1);leftBox(:,width)=twoColors(2)*ones(width,1);keyboardrightBox=fliplr(flipud(leftBox));%insert nonius structure into imagesleft(mdiv2-radius:mdiv2+radius,ndiv2-radius:ndiv2+radius)=leftBox;right(mdiv2-radius:mdiv2+radius,ndiv2-radius:ndiv2+radius)=rightBox;