function [textItems, type] = loadTextFile(fileName, path)% [textItems, type] = loadTextFile(fileName, [path]): loads text into cell array%%	The text file containing the list of text items should have the following format:%%	some text 1		text1Type%	some text 2		text2type%	etc...%%   some text N is a string (no tabs allowed!), textNType is anything (usually an %	integer code indicating what type of stimulus that some text N is.).  The two %	should be separated by a tab.%%% NOTE: if you edit the text file listing the text items in matlab,% make sure that you turn off the "tabs to spaces" option- the text item% and type must be separated by a real tab.%% 99.03.09 RFDoldPath = pwd;if exist('path', 'var')    cd(path);endfp = fopen(fileName, 'rt');i = 1;while ~feof(fp)    line = fgetl(fp);    % line will be -1 if eof is hit and may be empty if there are blank lines    if isstr(line) & ~isempty(line)        [textItems{i}, type{i}] = strtok(line,'	');        % strip leading tab from the type string        type{i} = strtok(type{i},'	');        i = i+1;    endendfclose(fp);cd(oldPath);