function [intensity] = DAC2intensity(DAC, DACtable)%%%%%%%%%%%%%%%%%%%%%%%  DAC2intensity  %%%%%%%%%%%%%%%%%%%%%%%%% [intensity] = DAC2intensity(DAC, DACtable)%% DAC should be a matrix with 3 columns, corresponding to%  the red, green, and blue DAC values. All values of DAC%  should be integers within [0, 255]%% This routine is obsolete. You should use DAC2RGB instead.if ( checkRange(DAC,[0 255])==0 ) error('DAC values should be within [0,255].');endintensityOne = DACtable( DAC(:,1)+1, 1 );intensityTwo = DACtable( DAC(:,2)+1, 2 );intensityThree = DACtable( DAC(:,3)+1, 3 );intensity = [intensityOne intensityTwo intensityThree];