%% to download measurements from spectraScan650%% These are the measures where I check the push-pull MT experiment.%% cmeter is in version 5 of Brainard's UCSB psychophysical toolbox%% After initializing colorimeter, it should say "remote mode"%% on the LCD display. Now we need to issue command strings to the colorimeter%% to retrieve data from files.%% The command strings are documented on page B-17 of the manual.s = cmeter('Init');cmeter('Cmd', 'R103');cmeter('XYZ')wrat12 = cmeter('Spectrum');         cmeter('Cmd', 'R104');cmeter('XYZ')wrat8 = cmeter('Spectrum');         cmeter('Cmd', 'R105');cmeter('XYZ')white = cmeter('Spectrum'); save wratten wrat12 wrat8 whitereturn    cmeter('Cmd', 'R96');bkg = cmeter('Spectrum');         cmeter('Cmd', 'R97');bkgY10p = cmeter('Spectrum');         cmeter('Cmd', 'R98');bkgY10m = cmeter('Spectrum');         cmeter('Cmd', 'R99');bkgY10S50p = cmeter('Spectrum');         cmeter('Cmd', 'R100');bkgY10S50m = cmeter('Spectrum');         cmeter('Cmd', 'R101');bkgY10Y20p = cmeter('Spectrum');         cmeter('Cmd', 'R102');bkgY10Y20m = cmeter('Spectrum');         s = cmeter('Init');cmeter('Cmd', 'R93');cmeter('XYZ')r10bit = cmeter('Spectrum');         cmeter('Cmd', 'R94');cmeter('XYZ')g10bit = cmeter('Spectrum');         cmeter('Cmd', 'R95');cmeter('XYZ')b10bit = cmeter('Spectrum');         save specRaw r10bit g10bit b10bit returncmeter('Cmd', 'R96');bkg = cmeter('Spectrum');         cmeter('Cmd', 'R97');bkgY10p = cmeter('Spectrum');         cmeter('Cmd', 'R98');bkgY10m = cmeter('Spectrum');         cmeter('Cmd', 'R99');bkgY10S50p = cmeter('Spectrum');         cmeter('Cmd', 'R100');bkgY10S50m = cmeter('Spectrum');         cmeter('Cmd', 'R101');bkgY10Y20p = cmeter('Spectrum');         cmeter('Cmd', 'R102');bkgY10Y20m = cmeter('Spectrum');         save pushPullStimRaw bkg bkgY10m bkgY10p bkgY10S50p bkgY10S50m bkgY10Y20p bkgY10Y20m