% initialize variablesxsize = 1152;ysize = 870;xshift = 200;yshift = 150;% First check the center region calibration ([xsize/2 ysize/2],1,[0 255 85], 1, [100 0 0])calibration ([xsize/2 ysize/2],2,[0 255 85], 1, [0 100 0])calibration ([xsize/2 ysize/2],3,[0 255 85], 1, [0 0 100])checkcorners = 0;if (checkcorners)% Now we check the four corners% upper leftcalibration([xshift yshift], 1, [0 255 85])calibration([xshift yshift], 2, [0 255 85])calibration([xshift yshift], 3, [0 255 85])% lower leftcalibration([xshift ysize-yshift], 1, [0 255 85])calibration([xshift ysize-yshift], 2, [0 255 85])calibration([xshift ysize-yshift], 3, [0 255 85])% lower rightcalibration([xsize-xshift ysize-yshift], 1, [0 255 85])calibration([xsize-xshift ysize-yshift], 2, [0 255 85])calibration([xsize-xshift ysize-yshift], 3, [0 255 85])% upper rightcalibration([xsize-xshift yshift], 1, [0 255 85])calibration([xsize-xshift yshift], 2, [0 255 85])calibration([xsize-xshift yshift], 3, [0 255 85])end