function DAC = intensity2DAC(intensity, invLUT)%%%%%%%%%%%%%%%%%%%%%%%  intensity2DAC  %%%%%%%%%%%%%%%%%%%%%%%%% DAC = intensity2DAC(intensity, invLUT)%% intensity must be between [0,1]%% This is obsolete. Try use RGB2DAC instead.if (checkRange(intensity, [0 1])==0)  error('Intensity must be between [0,1].');end[rows, junk] = size(invLUT);intensityEntry = round(intensity*(rows-1));DAC1 = invLUT( intensityEntry(:,1)+1, 1 );DAC2 = invLUT( intensityEntry(:,2)+1, 2 );DAC3 = invLUT( intensityEntry(:,3)+1, 3 );DAC = [DAC1 DAC2 DAC3];