function invDACtable = invertDACtable(DACtable, numLevels)% invDACtable = invertDACtable(DACtable, numLevels)%% Create an inverse DAC table so that we can look up the % DAC values corresponding to an intensity value.%   numLevels specifies how many entries to create for the new% inverse table. All intensity entries are between 0 and 1.% DAC table should be a 255 by 3 matrix, with entries ranging% from 0 to 1.dacs = (0:255)';intensities = pointsOnLine(0,1,numLevels);red = [DACtable(:,1) dacs];green = [DACtable(:,2) dacs];blue = [DACtable(:,3) dacs];disp('Sorting red intensities ...');red = sortRows(red, 1);disp('Sorting green intensities ...');green = sortRows(green, 1);disp('Sorting blue intensities ...');blue = sortRows(blue, 1);disp('Creating red DACs ...');redDAC = tableLookUp(intensities, red, 1);disp('Creating green DACs ...');greenDAC = tableLookUp(intensities, green, 1);disp('Creating blue DACs ...');blueDAC = tableLookUp(intensities, blue, 1);invDACtable = round([redDAC(:,2) greenDAC(:,2) blueDAC(:,2)]);