% initialize variablesxsize = 1152;ysize = 870;xshift = 200;yshift = 150;% First check the center region at 2 DAC levels, for the 3 guns, and sum of% three gunscalibration ([xsize/2 ysize/2],1,[150 255 105],1, [128 128 128])   % redcalibration ([xsize/2 ysize/2],2,[150 255 105],1, [128 128 128])   % greencalibration ([xsize/2 ysize/2],3,[150 255 105],1, [128 128 128])   % bluecalibration ([xsize/2 ysize/2],[255 255 255],[150 255 105],1, [128 128 128])  % whiteif (0)% check upper middle and lower middlecalibration ([xsize/2 100],1,[255 255 105])calibration ([xsize/2 100],2,[255 255 105])calibration ([xsize/2 100],3,[255 255 105])calibration ([xsize/2 ysize-100],1,[255 255 105])calibration ([xsize/2 ysize-100],2,[255 255 105])calibration ([xsize/2 ysize-100],3,[255 255 105])endcheckcorners = 0;if (checkcorners)% Now we check the four corners% upcalibration([xsize/2 yshift], 1, [255 255 1])calibration([xsize/2 yshift], 2, [255 255 1])calibration([xsize/2 yshift], 3, [255 255 1])% downcalibration([xsize/2 ysize-yshift], 1, [255 255 1])calibration([xsize/2 ysize-yshift], 2, [255 255 1])calibration([xsize/2 ysize-yshift], 3, [255 255 1])% leftcalibration([xshift ysize/2], 1, [255 255 1])calibration([xshift ysize/2], 2, [255 255 1])calibration([xshift ysize/2], 3, [255 255 1])% rightcalibration([xsize-xshift ysize/2], 1, [255 255 1])calibration([xsize-xshift ysize/2], 2, [255 255 1])calibration([xsize-xshift ysize/2], 3, [255 255 1])end