%% Download measurements from spectraScan650% In this example I pull in 8 measurements for which% I know the file numbers.  Thanks Mei.%% after initializing colorimeter, it should say "remote mode"%% one the LCD display. Now we need to issue command strings to the colorimeter%% to retrieve data from files%% The command strings are documented on page B-17 of the manual.%% cmeter is in version 5 of Brainard's UCSB psychophysical toolboxs = cmeter('Init');                 %% initialize camera% Here is how you get a list of the files%cmeter('Cmd', 'D301')% First setup and measures% Change current file to XX, then get the spectrumcmeter('Cmd', 'R64');  r10bit = cmeter('Spectrum'); cmeter('Cmd', 'R65');	g10bit = cmeter('Spectrum'); cmeter('Cmd', 'R66');	b10bit = cmeter('Spectrum');   cmeter('Cmd', 'R67');w10bit = cmeter('Spectrum');       figure;hold on;plot(w10bit(:,1),w10bit(:,2),'w-');plot(r10bit(:,1),r10bit(:,2),'r-');plot(g10bit(:,1),g10bit(:,2),'g-');plot(b10bit(:,1),b10bit(:,2),'b-');hold offsave sanyo10specB w10bit r10bit g10bit b10bit% Second setup and measurescmeter('Cmd', 'R68');  r10bit = cmeter('Spectrum'); cmeter('Cmd', 'R69');	g10bit = cmeter('Spectrum'); cmeter('Cmd', 'R70');	b10bit = cmeter('Spectrum');   cmeter('Cmd', 'R71');w10bit = cmeter('Spectrum');       figure;hold on;plot(w10bit(:,1),w10bit(:,2),'w-');plot(r10bit(:,1),r10bit(:,2),'r-');plot(g10bit(:,1),g10bit(:,2),'g-');plot(b10bit(:,1),b10bit(:,2),'b-');hold offsave sanyo10specC w10bit r10bit g10bit b10bit