function [gunSpects, wavelengths, xyz] = cmeterNECspectra(tsize, gunColors, monitor, synchFreq)%% [gunSpects, wavelengths, xyz] = cmeterNECspectra(tsize, gunColors, monitor, synchFreq)%% AUTHOR:  		Heidi Baseler% DATE:			02.12.98% HISTORY:		Adapted for NEC LCD2000 from "cmeterSpectraCalib.m"%				written by Xuemei Zhang on Turquoise, 08.98%% PURPOSE: 		Measure spectral power distribution of display guns using cmeter%% INPUTS:% tsize:		Size of target square, as [ysize xsize].%          		If tsize has 4 elements, treat as rectangle:%          		[ysize xsize ycenter xcenter].%         	 	If tsize has 1 element, target is a square.% gunColors:	Which guns to measure. Should be in the range [0 1].%              	For example, to measure red gun use [1 0 0].% monitor:		Use 0 for console, 1 for second monitor (check monitor numbers)% synchFreq:	Refresh rate of monitor (can be floating point number)if (length(tsize)==1)  tsize = [tsize tsize];end% Display a small target so that we can aim and focus camera:[screenPtr, screenRect] = screen(monitor, 'OpenWindow', 0);HideCursor;if (length(tsize)==4)  xc = tsize(4);  yc = tsize(3);else  xc = (screenRect(RectRight) - screenRect(RectLeft))/2;  yc = (screenRect(RectBottom) - screenRect(RectTop))/2;endtargetRect([RectTop RectLeft RectBottom RectRight])= ...           [yc xc yc xc]+[-1 -1 1 1]*50;screen(screenPtr, 'FillRect', 1, targetRect);targetClut = 1;bgClut = 0;textClut = 255;targetMap = gray(256)*255;targetMap(bgClut+1,:) = [0 0 0];		   targetMap(targetClut+1,:) = gunColors(1,:);		   targetMap(textClut+1,:) = gunColors(1,:);	  screen(screenPtr, 'SetClut', targetMap);		   screen(screenPtr, 'TextFont', 20);screen(screenPtr, 'TextSize', 16);screen(screenPtr, 'TextStyle', 1);screen(screenPtr, 'DrawText', 'Aim and focus camera, then press any key', ...                               xc-150, yc+350, 255);GetKey;%% erase text, draw targettargetMap(textClut+1,:) = targetMap(bgClut+1,:);	targetRect([RectTop RectLeft RectBottom RectRight])= ...           [yc xc yc xc]+[-tsize(1) -tsize(2) tsize(1) tsize(2)]/2;screen(screenPtr, 'FillRect', 1, targetRect);screen(screenPtr, 'SetClut', targetMap);		   % prepare cameras = cmeter('Init');                 %% initialize cameracmeter('SetParams', 0, 0, 1);       %% set synch,integration time,avgcnt% This part just won't work, so we set the monitor frequency up above as an input%%%%%% ABP HAB 02.12.98 [synchFreq, s] = cmeterGetSynch;%%%%%if (s~=0)%%%%%  error('Synch measurement failed.');%%%%%  screen(screenPtr, 'Close');%%%%%% end%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%% spectral measurementavgcnt = 5;gunSpects = zeros(101, size(gunColors,1));xyz = zeros(3, size(gunColors,1));for gun = 1:size(gunColors,1)%  targetMap([bgClut; textClut]+1,:) = gunColors(gun,:);  targetMap(targetClut+1,:) = gunColors(gun,:);  screen(screenPtr, 'Gamma', targetMap);		     screen(screenPtr, 'SetClut', gray(256)*255);		       integrationTime = cmeterGetTime(synchFreq);  cmeter('SetParams', 0, integrationTime, avgcnt);  s = cmeter('Measure', integrationTime*(avgcnt+2));    spect = cmeter('Spectrum');         %% get spectrum  gunSpects(:,gun) = spect(:,2);  xyz(:, gun) = cmeter('XYZ');endwavelengths = spect(:,1);ShowCursor;screen(screenPtr, 'Close');