% Script to test that the gamma correction is successful% HAB, 02.16.98load GammaFeb98monitor = 1;synchFreq = 73.8		% Refresh rate of the NEC MultiSync LCD2000 - 02.12.98targetSize = [1024 1280];% Test red gungunColors = [1 0 0];inds = 1:93:1024;linFBvalues = 1023 * invGamma10(inds,1);tstGammaValues = cmeterNECgamma(targetSize, gunColors, linFBvalues, monitor, synchFreq);% Plot outputplot(linFBvalues)figure;plot(tstGammaValues)