%ShowScanDemorunPriority = 7;%Generate a grating stimulusclear scan%Generate display structuremonitor = 'NecLcd2000'; position = 'room 452';card = 'Radius10-bit';resolution = '640x480, 67Hz';display = initDisplayStruct(monitor,card,resolution,position);display.screenNumber=1;%Grating stimulus parameters.params = makeDemoGratingParams;%Create the ballistic scan structure with 5 second stimuliparams.contrast = 0.75;params.duration = 5;stimulus1= makeFlickerGrating(display,params);stimulus2 = makeDriftGrating(display,params);% let's show 3 cycles of the scan stimuliballisticScan = createBallScanStruct(stimulus1,stimulus2,3);%Open the screendisplay = openScreen(display);%Note: no need to store images off screen - showScan does this for you.%NOTE: you must check the timing and play with the stimuli generation%to ensure proper duration of entire scantiming =showBallisticScan(display,ballisticScan,runPriority)% Close the one on-screen and many off-screen windowsSCREEN('CloseAll');