%ShowStimulusDemorunPriority = 0;  %0 for low priority, 7 for high priorityclear params%Grating stimulus parameters.params = makeDemoGratingParams;%Generate display structure%monitor = 'SanyoLCD';monitor = 'NecLcd2000';%monitor = 'Multiscan720';%position = 'outside bore';position = 'room 452';card = 'Radius10-bit';%card = 'BuiltIn8-bit';resolution = '640x480, 67Hz';% resolution = '832x624, 75Hz';display = initDisplayStruct(monitor,card,resolution,position);display.screenNumber =1;   %should be 0 or 1display.stepDac = 4;display.midDac = 0;%Generate a grating stimulus.%stimulus = makeDriftGrating(display,params);    % colormap  animationstimulus = makeFlickerGrating(display,params);  % colormap  animation%Open the screendisplay = openScreen(display);%Store the images off screenstimulus=createImagePointers(display,stimulus);clickMouse2Begin(display,'showStimulus Demo');%show the stimulusshowStimulus(display,stimulus,runPriority);%Close the one on-screen and many off-screen windowsSCREEN('CloseAll');