function params = makeDemoGratingParamsparams.size = [6,6];          %degrees of visual angleparams.spatialFrequency = 1/3;%c/degparams.spatialPhase =pi/2;    %cosine phase w/ center of screenparams.orientation = 0;       %radians (0 is vertical)params.temporalFrequency = .1; %Hzparams.temporalPhase = 0;     %sin phase with start of params.params.duration = 2;          %secondsparams.contrast = 1;          %Michelson contrastparams.fixationPoint = 1;