function countDownStereo(display,startSecs,startScan)% function countDown(display,starSecs,startScan)% % Purpose:  Counts down from secs to 1,%           displaying the numbers on the%           screen above the fixation point.% This is almost identical to countDown except that the string% display routines are the stereo string display functions% Author: Backus and Fleetif ~exist('startScan')	startScan = -1;endloc = display.rect([3,4])/2;%SCREEN(display.windowPtr,'TextStyle',1);if (startSecs>0)	startTime=getSecs;    for i=startSecs:-1:1		txt=num2str(i);		if (i~=startSecs)			dispStrInCtrStereo(display,num2str(i+1),0.5,'background');		end		dispStrInCtrStereo(display,num2str(i),0.5,'white');		waitTill(startSecs-i+1,startTime);		if i == startScan+1			StartScan;			dispStrInCtrStereo(display,'Start Scan',0.6,'white');		end    endenddispStrInCtrStereo(display,'1',0.5,'background');dispStrInCtrStereo(display,'Start Scan',0.6,'background');SCREEN(display.windowPtr,'TextSize',12);%SCREEN(display.windowPtr,'TextStyle',0);