function trial = createBlankTrial(display,templateTrial,fixation)%%trial = createBlankTrial(display,templateTrial,[fixation])%% AUTHOR: BOYNTON%% default fixation is%   fixation.colors = [1 2];%	fixation.size = 0.5;%	fixation.position = [0 0];	if ~exist('fixation')	fixation.colors = [1 2];	fixation.size = 0.5;	fixation.position = [0 0];endtrial = templateTrial;%replace the images and colormapsimgSize = size(templateTrial.stimulus(1).image(:,:,1));img = display.reservedColor(findName(display.reservedColor,'background')).fbVal * ones(imgSize);img = insertFixation(display,img,fixation);for stimNum = 1:2	trial.stimulus(stimNum).image = uint8(img);	trial.stimulus(stimNum).cmap = uint8(greyCmap(display));	trial.stimulus(stimNum).seq = [1,-1*ones(1,length(templateTrial.stimulus(stimNum).seq-1))];endtrial.ISI = templateTrial.ISI;trial.responseInterval = templateTrial.responseInterval;if isfield(templateTrial,'ISIstimulus')	trial.ISIstimulus = templateTrial.ISIstimulus;end