function dispStrInCtrStereo(display,dispString,vLoc,colorName,textSize)% dispStrCtrStereo(display,dispString,vLoc,colorName,textSize)%% simple variation of dispStringInCenter to display strings% in the centers of the left and right half planes for binocular viewing% Authors: Backus and Fleetif ~exist('vLoc')	vLoc = 0.5;endif ~exist('colorName')	colorName = 'white';endif ~exist('textSize')	textSize = 20;endSCREEN(display.windowPtr,'TextSize',textSize);charWidth = textSize/4.5;col = display.reservedColor(findName(display.reservedColor,colorName)).fbVal;loc(1) = display.rect(3)/4-charWidth*length(dispString);loc(2) = display.rect(4)*vLoc;loc(3) = 3*display.rect(3)/4-charWidth*length(dispString);SCREEN(display.windowPtr,'DrawText',dispString,loc(1),loc(2),col);SCREEN(display.windowPtr,'DrawText',dispString,loc(3),loc(2),col);