function display = display452;% % AUTHOR: Wandell% PURPOSE:%   Generate display structure for NEC LCD in room 452%   % monitor = 'NecLcd2000';position = 'room 452';card = 'Radius10-bit';%resolution = '832x624, 75Hz';resolution = '640x480, 67Hz';display = initDisplayStruct(monitor,card,resolution,position);display.screenNumber =1;   %should be 0 for mac or 1 for auxiliary NEC LCDfprintf('\n\ndisplay is set for NEC LCD attached to Vermillion \n \n');